"public class TabLayoutActivity extends BaseActivity { 
" " 
" " @BindView(R.id.vp_tab_layout) 
" " ViewPager vpTabLayout; 
" " 
" " @BindView(R.id.tb_tab_layout) 
" " TabLayout tbTabLayout; 
" " 
" " private List<MyFragment> fragments; 
" " 
" " private ViewPagerAdapter mAdapter; 
" " 
" " @Override 
" " protected int getContentLayout() { 
" " return R.layout.activity_tab_layout; 
" " } 
" " 
" " @Override 
" " protected void initViews() { 
" " super.initViews(); 
" " fragments = new ArrayList<>(); 
" " for (int i = 0; i < 12; i++) { 
" " fragments.add(MyFragment.newInstance("标题" + i, "内容" + i)); 
" " } 
" " 
" " mAdapter = new ViewPagerAdapter(getSupportFragmentManager(), fragments); 
" " vpTabLayout.setAdapter(mAdapter); 
" " tbTabLayout.setupWithViewPager(vpTabLayout); 
" " // 设置固定的 
" " // tbTabLayout.setTabMode(TabLayout.MODE_FIXED); 
" " // 可滚动的 
" " tbTabLayout.setTabMode(TabLayout.MODE_SCROLLABLE); 
" " } 
" " 
" "}
"
"public class MyFragment extends Fragment { 
" " 
" " public static final String TITLE = "title"; 
" " 
" " public static final String CONTENT = "content"; 
" " 
" " private Context mContext; 
" " 
" " private TextView textView; 
" " 
" " private String title; 
" " 
" " private String content; 
" " 
" " public static MyFragment newInstance(String title, String content) { 
" " Bundle bundle = new Bundle(); 
" " bundle.putString(TITLE, title); 
" " bundle.putString(CONTENT, content); 
" " MyFragment fragment = new MyFragment(); 
" " fragment.setArguments(bundle); 
" " return fragment; 
" " } 
" " 
" " @Override 
" " public void onCreate(@Nullable Bundle savedInstanceState) { 
" " super.onCreate(savedInstanceState); 
" " mContext = getActivity(); 
" " } 
" " 
" " @Nullable 
" " @Override 
" " public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, 
" " @Nullable Bundle savedInstanceState) { 
" " textView = new TextView(mContext); 
" " textView.setTextColor(Color.RED); 
" " textView.setTextSize(25); 
" " textView.setGravity(Gravity.CENTER); 
" " return textView; 
" " } 
" " 
" " @Override 
" " public void onActivityCreated(@Nullable Bundle savedInstanceState) { 
" " super.onActivityCreated(savedInstanceState); 
" " Bundle bundle = getArguments(); 
" " if (bundle == null) { 
" " return; 
" " } 
" " title = bundle.getString(TITLE); 
" " content = bundle.getString(CONTENT); 
" " textView.setText(content); 
" " } 
" "}
"
"public class ViewPagerAdapter extends FragmentPagerAdapter { 
" " 
" " private List<MyFragment> fragments; 
" " 
" " public ViewPagerAdapter(FragmentManager fm, List<MyFragment> fragments) { 
" " super(fm); 
" " this.fragments = fragments; 
" " } 
" " 
" " @Override 
" " public Fragment getItem(int position) { 
" " return fragments.get(position); 
" " } 
" " 
" " @Override 
" " public int getCount() { 
" " return fragments == null ? 0 : fragments.size(); 
" " } 
" " 
" " /** 
" " * 得到页面的标题 
" " * 
" " * @param position 
" " * @return 
" " */ 
" " @Override 
" " public CharSequence getPageTitle(int position) { 
" " return fragments.get(position).getArguments().getString(MyFragment.TITLE); 
" " } 
" " 
" "}"
